var gulp = require('gulp');


gulp.task('js', function() {
  return gulp.src('../../js/*.js')
  .pipe(gulp.dest('../../assets/'))
});

gulp.task('css', function() {
  return gulp.src('../../css/*.css')
  .pipe(gulp.dest('../../assets/'))
});

gulp.task("default", function() {
  gulp.watch('../../css/*.css', ['css']);
  gulp.watch('../../js/*.js', ['js']);
});
